mapper.update:
UPDATE t_claim
SET claim_title = #{title}, claim_content = #{content}, claim_type = #{type}, claim_file = #{file},
	user_emailID = #{emailID}, user_emailDomain = #{emailDomain},
	user_phone1 = #{phone1}, user_phone2 = #{phone2}, user_phone3 = #{phone3},
	claim_date = #{visitDate}, claim_store = #{visitStore}
WHERE claim_no = #{no}

UPDATE t_claim
SET view_cnt = view_cnt + 1
WHERE claim_no = #{no}
