mapper.select:

WHERE borad_no = #{boardNo}

	
		AND file_name =  'noticeFile' 
	
	
		AND file_name =  'claimFile'

mapper.update:
UPDATE t_file
SET file_name = #{name}, file_ori = #{fileOriName}, file_save = #{fileSaveName}, 
	file_size = #{fileSize}, file_path = #{filePath}, file_type = #{contentType}
WHERE borad_no = #{boardNo}
	
		
			AND file_name =  'noticeFile' ;
		
		
			AND file_name =  'claimFile' ;

mapper.delete:
DELETE FROM t_file WHERE borad_no = #{boardNo}
AND file_name = 'noticeFile'
AND file_name = 'claimFile'
