mapper.update:
UPDATE t_notice
SET notice_title = #{title}, notice_content = #{content}, 
	notice_type = #{type}, file_type = #{fileOX}, reg_at = #{regDate}
WHERE notice_no = #{no}

UPDATE t_notice
SET file_type =  'O' 
WHERE notice_no = #{no}

UPDATE t_notice
SET file_type =  'X' 
WHERE notice_no = #{no}

mapper.delete:
		DELETE FROM t_notice
		WHERE notice_no IN
			
       			#{item}

mapper.update:
UPDATE t_notice
SET view_cnt = view_cnt + 1
WHERE notice_no = #{no}

mapper.select:
    	select notice_no, notice_writer, notice_title, notice_content, notice_type, file_type, view_cnt, to_char(reg_at, 'yy-mm-dd') as reg_at 
    	from (select * from t_notice order by reg_at desc)
    	
		where rownum <=5
