mapper.select:
select order_no, sto_name, user_id, order_orderPrice, order_finalPrice, order_menu, order_eat_type, order_payment, order_status, to_char(reg_at, 'DY HH24:MI:SS') as reg_at
from ( select * from t_order order by order_no asc)
where order_status in (1, 2) AND ROWNUM <= 4

select order_no, sto_name, user_id, order_orderPrice, order_finalPrice, order_menu, order_eat_type, order_payment, order_status, to_char(reg_at, 'DY HH24:MI:SS') as reg_at
from ( select * from t_order order by order_no asc)
where order_status = 1 AND ROWNUM <= 1
